import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { blog_data, comments_data } from "../assets/assets";
import Navbar from "../components/Navbar";
import Moment from "moment";
import Footer from "../components/Footer";
import Loader from "../components/Loader";

function Blog() {
  const { id } = useParams();
  const [data, setData] = useState(null);
  const [comments, setComments] = useState([]);
  const [name, setName] = useState("");
  const [content, setContent] = useState("");

  // console.log(id);

  const fetchBlogData = async () => {
    const data = blog_data.find(item => item.id === id);
    setData(data);
  };


  const fetchComments = async () => {
    setComments(comments_data);
  };


  const addComment = async (e) => {
    e.preventdefault();
  };


  useEffect(() => {
    fetchBlogData();
    fetchComments();
  }, [id]);

  return data ?(
    <div className="relative">
      <Navbar />

      {/*Full Blog detail Page*/}
      <div className="text-center mt-20  text-gray-600">


        <p className="text-primary py-4 font-medium">
          Published on {Moment(data.createdAt).format("MMM Do YYYY")}
        </p>


        <h1 className="text-2xl sm:text-5xl font-semibold max-w-2xl mx-auto text-gray-800">
          {data.title}
        </h1>


        <h2 className="my-5 max-w-lg truncate mx-auto">{data.Title}</h2>
        <p className="inline-block py-1 px-4 rounded-full mb-6 border text-sm border-primary/35 bg-primary/5 font-medium text-primary">
          Michle Brown
        </p>
      </div>


      <div className="mx-5 max-w-5xl md:mx-auto my-10 pl-20 mt-6">
        <img src={data.image} alt="" className="rounded-3xl mb-5" />


        <div
          className="rich-text max-w-3xl"
          dangerouslySetInnerHTML={{ __html: data.description }}>
          </div>


        {/* comment sections */}
        <div className="mt-4 mb-10 ml-2 max-w-3xl mx-auto">
          <p className="font-semibold mb-3">Comments ({comments.length})</p>

          <div className="flex flex-col gap-4">
            {comments.map((item, index) => (
              <div
                key={index}
                className="relative bg-primary/2 border border-primary/5 max-w-xl    p-4 rounded text-gray-600">
                <div className="flex items-center gap-2 mb-2">
                  <img
                    src="https://c4.wallpaperflare.com/wallpaper/116/943/740/the-explosion-superhero-tony-stark-tony-stark-wallpaper-preview.jpg"
                    alt=""
                    className="w-10"/>
                  <p className="font-medium">{item.name}</p>
                </div>
                
                <p className="text-sm max-w-md ml-1">{item.content}</p>
                <div className="absolute right-4 bottom-3 flex items-center gap-2 text-xs">
                  {Moment(item.createdAt).fromNow()}
                </div>

              </div>
            ))}
          </div>
        </div>

        {/*Add comments section*/}
        <div className="max-w-3xl mx-auto ml-2">
          <p className="font-semibold mb-4">Add Your comment</p>
          <form
            onSubmit={addComment}
            className="flex flex-col items-start gap-4 max-w-lg">


            <input
              onChange={(e) => setName(e.target.value)}
              value={name}
              type="text"
              placeholder="name"
              required
              className="w-full p-2 border border-gray-300 rounded outline-none"/>


            <textarea
              onChange={(e) => setContent(e.target.value)}
              value={content}
              className="w-full p-2 border bg-gray-100 rounded outline-none h-48"
              placeholder="comment"
              required>
              </textarea>


            <button
              className="bg-primary text-white rounded p-2 px-8 hover:scale-102 transition-all cursor-pointer"
              type="submit">
              Submit
            </button>
          </form>
        </div>


        {/* share button */}
        <div className="my-24 max-w-3xl ml-2 mx-auto ">
          <p className="font-semibold mb-7">
            Share This artical on social media
          </p>
          
          <div className="flex gap-2">
            <img
              className="rounded-full"
              src="data:image/png;base64,"
              alt=""
              width={50}
            />

            <img
              className="rounded-full"
              src="data:image/jpeg;base64,"
              alt=""
              width={50}
            />

            <img
              className="rounded-full"
              src="data:image/jpeg;base64,"
              alt=""
              width={50}
            />
          </div>
        </div>
      </div>
      <Footer />
    </div>
  ):<div><Loader/></div>;
}

export default Blog;
